import React, { useState } from 'react';
import { preDesignedScenarios } from '../templates/preDesignedScenarios';
import './ScenarioSelection.css';

const ScenarioSelection = ({ onSelectScenario, onCreateOwn, isApiKeySet }) => {
  const [showCreateOptions, setShowCreateOptions] = useState(false);

  const handleScenarioClick = (scenario) => {
    onSelectScenario(scenario);
  };

  const toggleCreateOptions = () => {
    setShowCreateOptions(!showCreateOptions);
  };

  return (
    <div className="scenario-selection">
      <h2>Choose Your Adventure</h2>
      <p>Select a pre-designed scenario or create your own unique experience</p>

      <div className="scenarios-grid">
        {preDesignedScenarios.map((scenario) => (
          <div 
            key={scenario.id} 
            className="scenario-card"
          >
            <h3>{scenario.title}</h3>
            <p>{scenario.description}</p>
            <div className="scenario-footer">
              <button 
                className="select-btn"
                onClick={() => handleScenarioClick(scenario)}
              >
                Play Now
              </button>
            </div>
          </div>
        ))}

        <div className="scenario-card create-own">
          <h3>Create Your Own</h3>
          <p>Design a custom character and scenario from scratch</p>
          <div className="scenario-footer">
            {!showCreateOptions ? (
              <button 
                className="create-btn"
                onClick={toggleCreateOptions}
              >
                Create
              </button>
            ) : (
              <div className="create-options">
                <button 
                  className="step-by-step-btn"
                  onClick={() => onCreateOwn('step')}
                >
                  Step-by-Step
                </button>
                <button 
                  className="quick-setup-btn"
                  onClick={() => onCreateOwn('quick')}
                >
                  Quick Setup
                </button>
              </div>
            )}
          </div>
        </div>
      </div>

      {!isApiKeySet && (
        <div className="api-key-overlay">
          <div className="api-key-message">
            <p>Add an API key to begin</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default ScenarioSelection;
