import React, { useState, useEffect } from 'react';
import VoiceSelector from './VoiceSelector';
import './Settings.css';

const Settings = ({ 
  apiKey, 
  setApiKey, 
  isApiKeySet, 
  setIsApiKeySet, 
  onClose,
  testApiKey,
  isTestingApi,
  apiTestResult,
  onSaveApiKey,
  // Added ElevenLabs props
  elevenLabsApiKey,
  setElevenLabsApiKey,
  isVoiceEnabled,
  setIsVoiceEnabled,
  selectedVoice,
  setSelectedVoice
}) => {
  const [localApiKey, setLocalApiKey] = useState(apiKey);
  const [showApiKey, setShowApiKey] = useState(false);
  // Add ElevenLabs state
  const [localElevenLabsApiKey, setLocalElevenLabsApiKey] = useState(elevenLabsApiKey || '');
  const [showElevenLabsApiKey, setShowElevenLabsApiKey] = useState(false);
  const [isTestingVoiceApi, setIsTestingVoiceApi] = useState(false);
  const [voiceApiTestResult, setVoiceApiTestResult] = useState(null);
  const [showVoiceSelector, setShowVoiceSelector] = useState(false);

  useEffect(() => {
    setLocalApiKey(apiKey);
    setLocalElevenLabsApiKey(elevenLabsApiKey || '');
  }, [apiKey, elevenLabsApiKey]);

  // Check if we should show the voice selector
  useEffect(() => {
    if (elevenLabsApiKey && isVoiceEnabled) {
      setShowVoiceSelector(true);
    }
  }, [elevenLabsApiKey, isVoiceEnabled]);

  const handleSaveApiKey = (e) => {
    e.preventDefault();
    onSaveApiKey(localApiKey);
  };

  // Add handler for ElevenLabs API key
  const handleSaveElevenLabsApiKey = () => {
    localStorage.setItem('elevenlabs_api_key', localElevenLabsApiKey);
    setElevenLabsApiKey(localElevenLabsApiKey);
  };

  // Test ElevenLabs API key functionality
  const testVoiceApiKey = async () => {
    if (!localElevenLabsApiKey) {
      setVoiceApiTestResult({
        success: false,
        message: 'Please enter an ElevenLabs API key first'
      });
      return;
    }

    setIsTestingVoiceApi(true);
    setVoiceApiTestResult(null);

    try {
      // Test the API by fetching voices
      const response = await fetch('https://api.elevenlabs.io/v1/voices', {
        method: 'GET',
        headers: {
          'Accept': 'application/json',
          'xi-api-key': localElevenLabsApiKey
        }
      });

      if (!response.ok) {
        throw new Error(`API error: ${response.status}`);
      }

      const data = await response.json();

      setVoiceApiTestResult({
        success: true,
        message: 'ElevenLabs API key is valid! Found ' + (data.voices?.length || 0) + ' voices.',
        voices: data.voices
      });

      // Show voice selector after successful test
      setShowVoiceSelector(true);

    } catch (error) {
      console.error('Voice API test error:', error);
      let errorMessage = error.message;

      // Make error messages more user-friendly
      if (error.message.includes('401')) {
        errorMessage = 'API key is invalid or has expired.';
      } else if (error.message.includes('403')) {
        errorMessage = 'API key does not have sufficient permissions.';
      } else if (error.message.includes('429')) {
        errorMessage = 'Rate limit exceeded. Please wait a moment and try again.';
      }

      setVoiceApiTestResult({
        success: false,
        message: `Voice API test failed: ${errorMessage}`
      });

      // Hide voice selector on error
      setShowVoiceSelector(false);
    } finally {
      setIsTestingVoiceApi(false);
    }
  };

  // Handle voice selection
  const handleSelectVoice = (voice) => {
    setSelectedVoice(voice);

    // Save selected voice to localStorage
    localStorage.setItem('selected_voice', JSON.stringify({
      voice_id: voice.voice_id,
      name: voice.name
    }));
  };

  return (
    <div className="settings-overlay">
      <div className="settings-panel">
        <div className="settings-header">
          <h2>
            <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
              <path d="M12.22 2h-.44a2 2 0 0 0-2 2v.18a2 2 0 0 1-1 1.73l-.43.25a2 2 0 0 1-2 0l-.15-.08a2 2 0 0 0-2.73.73l-.22.38a2 2 0 0 0 .73 2.73l.15.1a2 2 0 0 1 1 1.72v.51a2 2 0 0 1-1 1.74l-.15.09a2 2 0 0 0-.73 2.73l.22.38a2 2 0 0 0 2.73.73l.15-.08a2 2 0 0 1 2 0l.43.25a2 2 0 0 1 1 1.73V20a2 2 0 0 0 2 2h.44a2 2 0 0 0 2-2v-.18a2 2 0 0 1 1-1.73l.43-.25a2 2 0 0 1 2 0l.15.08a2 2 0 0 0 2.73-.73l.22-.39a2 2 0 0 0-.73-2.73l-.15-.08a2 2 0 0 1-1-1.74v-.5a2 2 0 0 1 1-1.74l.15-.09a2 2 0 0 0 .73-2.73l-.22-.38a2 2 0 0 0-2.73-.73l-.15.08a2 2 0 0 1-2 0l-.43-.25a2 2 0 0 1-1-1.73V4a2 2 0 0 0-2-2z"></path>
              <circle cx="12" cy="12" r="3"></circle>
            </svg>
            Settings
          </h2>
          <button className="close-btn" onClick={onClose}>Ã—</button>
        </div>

        <div className="settings-content">
          <section className="api-key-section">
            <h3>
              <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <path d="M21 2l-2 2m-7.61 7.61a5.5 5.5 0 1 1-7.778 7.778 5.5 5.5 0 0 1 7.777-7.777zm0 0L15.5 7.5m0 0l3 3L22 7l-3-3m-3.5 3.5L19 4"></path>
              </svg>
              Gemini API Setup
            </h3>
            <p>Enter your Gemini API key to use with NarrativeForge. You can get a key from the <a href="https://aistudio.google.com/" target="_blank" rel="noopener noreferrer">Google AI Studio</a>.</p>

            <form onSubmit={handleSaveApiKey}>
              <div className="api-key-input">
                <label htmlFor="apiKey">Gemini API Key:</label>
                <div className="input-with-toggle">
                  <input 
                    id="apiKey"
                    type={showApiKey ? "text" : "password"}
                    value={localApiKey} 
                    onChange={(e) => setLocalApiKey(e.target.value)}
                    placeholder="Enter your API key"
                    autoComplete="off"
                  />
                  <button 
                    type="button" 
                    className="toggle-visibility"
                    onClick={() => setShowApiKey(!showApiKey)}
                  >
                    {showApiKey ? "Hide" : "Show"}
                  </button>
                </div>
              </div>

              <div className="api-key-actions">
                <button 
                  type="button" 
                  className="test-btn"
                  onClick={() => testApiKey()}
                  disabled={isTestingApi || !localApiKey}
                >
                  {isTestingApi ? (
                    <>
                      <div className="loading-spinner"></div>
                      Testing...
                    </>
                  ) : (
                    <>
                      <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                        <path d="M22 11.08V12a10 10 0 1 1-5.93-9.14"></path>
                        <polyline points="22 4 12 14.01 9 11.01"></polyline>
                      </svg>
                      Test Key
                    </>
                  )}
                </button>

                <button 
                  type="submit" 
                  className="save-btn"
                  disabled={!localApiKey}
                >
                  <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                    <path d="M19 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h11l5 5v11a2 2 0 0 1-2 2z"></path>
                    <polyline points="17 21 17 13 7 13 7 21"></polyline>
                    <polyline points="7 3 7 8 15 8"></polyline>
                  </svg>
                  Save Key
                </button>
              </div>
            </form>

            {apiTestResult && (
              <div className={`api-test-result ${apiTestResult.success ? 'success' : 'error'}`}>
                <p>{apiTestResult.message}</p>
                {apiTestResult.success && apiTestResult.response && (
                  <div className="api-response">
                    <small>Model response:</small>
                    <p>{apiTestResult.response.substring(0, 100)}...</p>
                  </div>
                )}
              </div>
            )}
          </section>

          {/* Add ElevenLabs section */}
          <section className="voice-settings-section">
            <h3>
              <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <path d="M12 1a3 3 0 0 0-3 3v8a3 3 0 0 0 6 0V4a3 3 0 0 0-3-3z"></path>
                <path d="M19 10v2a7 7 0 0 1-14 0v-2"></path>
                <line x1="12" y1="19" x2="12" y2="23"></line>
                <line x1="8" y1="23" x2="16" y2="23"></line>
              </svg>
              Voice Settings (ElevenLabs)
            </h3>
            <p>Add voice to your roleplays with ElevenLabs. <a href="https://elevenlabs.io/" target="_blank" rel="noopener noreferrer">Get your API key from ElevenLabs</a>.</p>

            <div className="voice-settings">
              <div className="api-key-input">
                <label htmlFor="elevenLabsApiKey">ElevenLabs API Key:</label>
                <div className="input-with-toggle">
                  <input 
                    id="elevenLabsApiKey"
                    type={showElevenLabsApiKey ? "text" : "password"}
                    value={localElevenLabsApiKey} 
                    onChange={(e) => setLocalElevenLabsApiKey(e.target.value)}
                    placeholder="Enter your ElevenLabs API key"
                    autoComplete="off"
                  />
                  <button 
                    type="button" 
                    className="toggle-visibility"
                    onClick={() => setShowElevenLabsApiKey(!showElevenLabsApiKey)}
                  >
                    {showElevenLabsApiKey ? "Hide" : "Show"}
                  </button>
                </div>
              </div>

              <div className="api-key-actions">
                <button 
                  type="button" 
                  className="test-btn"
                  onClick={testVoiceApiKey}
                  disabled={isTestingVoiceApi || !localElevenLabsApiKey}
                >
                  {isTestingVoiceApi ? (
                    <>
                      <div className="loading-spinner"></div>
                      Testing...
                    </>
                  ) : (
                    <>
                      <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                        <path d="M22 11.08V12a10 10 0 1 1-5.93-9.14"></path>
                        <polyline points="22 4 12 14.01 9 11.01"></polyline>
                      </svg>
                      Test Voice Key
                    </>
                  )}
                </button>

                <button 
                  className="save-btn"
                  onClick={handleSaveElevenLabsApiKey}
                  disabled={!localElevenLabsApiKey}
                >
                  <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                    <path d="M19 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h11l5 5v11a2 2 0 0 1-2 2z"></path>
                    <polyline points="17 21 17 13 7 13 7 21"></polyline>
                    <polyline points="7 3 7 8 15 8"></polyline>
                  </svg>
                  Save Voice Key
                </button>
              </div>

              {voiceApiTestResult && (
                <div className={`api-test-result ${voiceApiTestResult.success ? 'success' : 'error'}`}>
                  <p>{voiceApiTestResult.message}</p>
                  {voiceApiTestResult.success && voiceApiTestResult.voices && (
                    <div className="api-response">
                      <small>Available voices:</small>
                      <p>{voiceApiTestResult.voices.slice(0, 3).map(voice => voice.name).join(', ')}{voiceApiTestResult.voices.length > 3 ? `, and ${voiceApiTestResult.voices.length - 3} more...` : ''}</p>
                    </div>
                  )}
                </div>
              )}

              <div className="form-group checkbox-group">
                <label className="checkbox-label">
                  <input
                    type="checkbox"
                    checked={isVoiceEnabled}
                    onChange={(e) => {
                      setIsVoiceEnabled(e.target.checked);
                      localStorage.setItem('voice_enabled', e.target.checked.toString());
                    }}
                  />
                  <span>Enable voice responses</span>
                </label>
                <p className="setting-description">When enabled, AI responses will be read aloud using ElevenLabs TTS.</p>
              </div>

              {/* Voice Selector */}
              {showVoiceSelector && (
                <div className="voice-selector-container">
                  <h4 className="voice-selector-title">Select Voice</h4>
                  <p className="voice-selector-description">Choose a voice for your AI responses. Click on a voice to preview and select it.</p>
                  <VoiceSelector 
                    elevenLabsApiKey={localElevenLabsApiKey}
                    selectedVoice={selectedVoice}
                    onSelectVoice={handleSelectVoice}
                    isLoading={isTestingVoiceApi}
                  />
                </div>
              )}
            </div>
          </section>

          <section className="about-section">
            <h3>
              <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <circle cx="12" cy="12" r="10"></circle>
                <line x1="12" y1="16" x2="12" y2="12"></line>
                <line x1="12" y1="8" x2="12.01" y2="8"></line>
              </svg>
              About NarrativeForge
            </h3>
            <p>NarrativeForge is a character-driven roleplay application created by Ryan Morrison. Create immersive storytelling experiences with detailed characters and scenarios.</p>
            <p className="version">Version 1.0.0</p>
          </section>
        </div>
      </div>
    </div>
  );
};

export default Settings;
