import React, { useState, useEffect } from 'react';
import QuickSetup from './components/SetupMode/QuickSetup';
import './components/SetupMode/QuickSetup.css';
import CharacterProfile from './components/CharacterProfile';
import ScenarioBuilder from './components/ScenarioBuilder';
import ChatInterface from './components/ChatInterface';
import ScenarioSelection from './components/ScenarioSelection';
import Settings from './components/Settings';
import ApiKeyWarning from './components/ApiKeyWarning';
import GeminiService from './services/geminiService';
import { defaultCharacter, defaultScenario } from './templates/defaultTemplate';
import './App.css';
import './improved-message-formatting.css'; // Import improved message formatting
import './social-media-style.css'; // Import social media styling
import './chat-auto-detect.css'; // Import auto-detect styling

function App() {
  const [currentStep, setCurrentStep] = useState('welcome'); // welcome, selection, character, scenario, chat, quickSetup
  const [character, setCharacter] = useState(null);
  const [scenario, setScenario] = useState(null);
  const [useTemplate, setUseTemplate] = useState(false);
  const [geminiService, setGeminiService] = useState(null);
  const [apiKey, setApiKey] = useState('');
  const [isApiKeySet, setIsApiKeySet] = useState(false);
  const [isTestingApi, setIsTestingApi] = useState(false);
  const [apiTestResult, setApiTestResult] = useState(null);
  const [showSettings, setShowSettings] = useState(false);

  // Initialize Gemini service when API key is set
  useEffect(() => {
    if (isApiKeySet) {
      const service = new GeminiService(apiKey);
      setGeminiService(service);
    }
  }, [apiKey, isApiKeySet]);

  // Initialize service for roleplay when character and scenario are ready
  useEffect(() => {
    if (geminiService && character && scenario && currentStep === 'chat') {
      geminiService.initialize(character, scenario);
    }
  }, [geminiService, character, scenario, currentStep]);

  // Test API key functionality
  const testApiKey = async () => {
    if (!apiKey) {
      setApiTestResult({
        success: false,
        message: 'Please enter an API key first'
      });
      return;
    }

    setIsTestingApi(true);
    setApiTestResult(null);

    try {
      const service = new GeminiService(apiKey);
      const response = await service.generateGeneric('Respond only with: "API test successful" - no other text.');

      setApiTestResult({
        success: true,
        message: 'API test successful!',
        response: response
      });

    } catch (error) {
      console.error('API test error:', error);
      let errorMessage = error.message;

      // Make error messages more user-friendly
      if (error.message.includes('401')) {
        errorMessage = 'API key is invalid or has expired.';
      } else if (error.message.includes('403')) {
        errorMessage = 'API key does not have access to the required model (gemini-1.5-flash).';
      } else if (error.message.includes('429')) {
        errorMessage = 'Rate limit exceeded. Please wait a moment and try again.';
      }

      setApiTestResult({
        success: false,
        message: `API test failed: ${errorMessage}`
      });
    } finally {
      setIsTestingApi(false);
    }
  };

  // Save API key
  const handleApiKeySubmit = async (e, goToQuickSetup = false) => {
    e.preventDefault();

    try {
      // Test the API key by making a simple call
      const service = new GeminiService(apiKey);
      await service.generateGeneric('Hello, can you confirm this API key works?');

      // If successful, save the API key and continue
      setIsApiKeySet(true);
      localStorage.setItem('gemini_api_key', apiKey);

      // Set the service
      setGeminiService(service);

      // If user chooses to use template, load it
      if (useTemplate) {
        setCharacter(defaultCharacter);
      }

      // Move to the next step - either quick setup or character based on parameter
      setCurrentStep(goToQuickSetup ? 'quickSetup' : 'character');
    } catch (error) {
      console.error('API key validation error:', error);
      alert(`Error with API key: ${error.message}. Please check your API key and try again.`);
    }
  };

  // Toggle settings panel
  const toggleSettings = () => {
    setShowSettings(!showSettings);
  };

  // Handle scenario selection
  const handleSelectScenario = (scenarioData) => {
    setCharacter(scenarioData.character);
    setScenario(scenarioData.scenario);
    setCurrentStep('chat');
  };

  // Handle 'Create your own' selection
  const handleCreateOwn = (mode) => {
    if (mode === 'quick') {
      setCurrentStep('quickSetup');
    } else {
      setCurrentStep('character');
    }
  };

  // Handle saving setup data (character and scenario together from quick setup)
  const handleSaveSetup = (characterData, scenarioData) => {
    setCharacter(characterData);
    setScenario(scenarioData);
    setCurrentStep('chat');
  };

  // Handle saving character data
  const handleSaveCharacter = (characterData) => {
    setCharacter(characterData);
    setCurrentStep('scenario');

    // If user chose to use template, also load scenario template
    if (useTemplate) {
      setScenario(defaultScenario);
    }
  };

  // Handle saving scenario data
  const handleSaveScenario = (scenarioData) => {
    setScenario(scenarioData);
    setCurrentStep('chat');
  };

  // Handle saving chat
  const handleSaveChat = (chatData) => {
    // Save chat functionality
    console.log('Saving chat:', chatData);
  };

  // Reset to welcome screen
  const handleReset = () => {
    if (window.confirm('Are you sure you want to start over? All current data will be lost.')) {
      setCurrentStep('welcome');
      setCharacter(null);
      setScenario(null);
    }
  };

  // Load API key from local storage on first render
  useEffect(() => {
    const savedApiKey = localStorage.getItem('gemini_api_key');
    if (savedApiKey) {
      setApiKey(savedApiKey);
      setIsApiKeySet(true);
    }
  }, []);

  return (
    <div className="app">
      <header className="app-header">
        <div className="header-left">
          <h1>NarrativeForge</h1>
          <p>Character-driven roleplay with Gemini AI</p>
        </div>
        <div className="header-right">
          <button className="settings-btn" onClick={toggleSettings}>
            ⚙️ Settings
          </button>
        </div>
      </header>

      {showSettings && (
        <Settings 
          apiKey={apiKey}
          setApiKey={setApiKey}
          isApiKeySet={isApiKeySet}
          setIsApiKeySet={setIsApiKeySet}
          onClose={toggleSettings}
          testApiKey={testApiKey}
          isTestingApi={isTestingApi}
          apiTestResult={apiTestResult}
        />
      )}

      <main className="app-content">
        {currentStep === 'welcome' && (
          <div className="welcome-screen">
            <h2>Welcome to NarrativeForge</h2>
            <p>Create immersive, character-driven roleplays powered by Google's Gemini 1.5 Flash AI.</p>

            {!isApiKeySet && (
              <ApiKeyWarning onOpenSettings={toggleSettings} />
            )}

            <div className="template-option">
              <label className="checkbox-label">
                <input 
                  type="checkbox" 
                  checked={useTemplate} 
                  onChange={(e) => setUseTemplate(e.target.checked)}
                />
                Use the example character and scenario template
              </label>
              <p className="template-description">Loads the pre-configured character and betrayal scenario you specified</p>
            </div>

            <div className="setup-buttons">
              <button 
                className="primary-btn" 
                onClick={() => isApiKeySet ? setCurrentStep('selection') : toggleSettings()}
              >
                Get Started
              </button>
              {isApiKeySet && (
                <button 
                  className="secondary-btn"
                  onClick={() => setCurrentStep('quickSetup')}
                >
                  Quick Setup Mode
                </button>
              )}
            </div>

            <div className="info-section">
              <h3>How it works:</h3>
              <ol>
                <li>Select a pre-designed scenario or create your own</li>
                <li>Customize your character profile with detailed background</li>
                <li>Set up a scenario with specific context and characters</li>
                <li>Engage in dynamic roleplay with AI-powered responses</li>
              </ol>
            </div>
          </div>
        )}

        {currentStep === 'selection' && (
          <ScenarioSelection 
            onSelectScenario={handleSelectScenario}
            onCreateOwn={handleCreateOwn}
          />
        )}

        {currentStep === 'character' && (
          <CharacterProfile 
            onSave={handleSaveCharacter} 
            initialData={useTemplate ? defaultCharacter : null}
            geminiService={geminiService}
            onReset={handleReset}
          />
        )}

        {currentStep === 'scenario' && (
          <ScenarioBuilder 
            onSave={handleSaveScenario} 
            initialData={useTemplate ? defaultScenario : null}
            geminiService={geminiService}
            onReset={handleReset}
          />
        )}

        {currentStep === 'quickSetup' && (
          <QuickSetup
            onSave={handleSaveSetup}
            geminiService={geminiService}
            onReset={handleReset}
          />
        )}

        {currentStep === 'chat' && (
          <ChatInterface 
            character={character}
            scenario={scenario}
            onSaveChat={handleSaveChat}
            geminiService={geminiService}
            onReset={handleReset}
          />
        )}
      </main>

      <footer className="app-footer">
        <div className="progress-indicator">
          <div className={`step ${currentStep === 'welcome' ? 'active' : ''}`}>Start</div>
          {currentStep === 'selection' && (
            <div className={`step active`}>Select Scenario</div>
          )}
          {currentStep !== 'quickSetup' && currentStep !== 'selection' && currentStep !== 'welcome' && currentStep !== 'chat' ? (
            <>
              <div className={`step ${currentStep === 'character' ? 'active' : ''}`}>Character</div>
              <div className={`step ${currentStep === 'scenario' ? 'active' : ''}`}>Scenario</div>
            </>
          ) : currentStep === 'quickSetup' ? (
            <div className={`step active`}>Quick Setup</div>
          ) : null}
          <div className={`step ${currentStep === 'chat' ? 'active' : ''}`}>Roleplay</div>
        </div>

        {currentStep !== 'welcome' && (
          <button className="reset-btn" onClick={handleReset}>
            Start Over
          </button>
        )}
      </footer>
    </div>
  );
}

export default App;
