import React, { useState, useEffect, useRef } from 'react';
import { Send, Settings, Music, BookOpen } from 'lucide-react';

const ChatInterface = ({ character, scenario, onSaveChat, geminiService }) => {
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [messageType, setMessageType] = useState('dialogue'); // dialogue, action, thought
  const messagesEndRef = useRef(null);

  // Initial system message when the scenario begins
  useEffect(() => {
    if (scenario && !messages.length) {
      // Create initial narrative message describing the scenario
      const initialMessage = {
        id: Date.now(),
        type: 'system',
        content: scenario.initialSituation,
        timestamp: new Date()
      };

      setMessages([initialMessage]);

      // Simulate AI thinking and then responding to set the scene
      handleAiResponse();
    }
  }, [scenario]);

  // Auto-scroll to bottom when messages change
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  // Send user message
  const handleSendMessage = () => {
    if (!inputValue.trim()) return;

    // Create user message object
    const userMessage = {
      id: Date.now(),
      sender: 'user',
      type: messageType,
      content: inputValue,
      timestamp: new Date()
    };

    // Add message to chat
    setMessages(prev => [...prev, userMessage]);

    // Clear input field
    setInputValue('');

    // Process with AI
    handleAiResponse(userMessage.content, userMessage.type);
  };

  // Handle key press (Enter to send)
  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  // Get AI response
  const handleAiResponse = async (userMessage, messageType) => {
    setIsTyping(true);

    try {
      let responseContent;

      // If we have a Gemini service, use it
      if (geminiService) {
        responseContent = await geminiService.generateResponse(userMessage, messageType);
      } else {
        // Fallback for development
        await new Promise(resolve => setTimeout(resolve, 1500));
        responseContent = "This is a placeholder response. In the full implementation, this would be a response from Google's Gemini 2.5 Flash LLM, responding appropriately to your character's situation.";
      }

      const aiMessage = {
        id: Date.now(),
        sender: 'ai',
        type: 'dialogue',
        content: responseContent,
        timestamp: new Date()
      };

      setMessages(prev => [...prev, aiMessage]);
    } catch (error) {
      console.error('Error getting AI response:', error);

      // Add error message
      const errorMessage = {
        id: Date.now(),
        sender: 'system',
        type: 'error',
        content: 'There was an error getting a response. Please try again.',
        timestamp: new Date()
      };

      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsTyping(false);
    }
  };

  // Format message based on type
  const formatMessage = (message) => {
    switch (message.type) {
      case 'system':
        return <p className="system-message">{message.content}</p>;
      case 'error':
        return <p className="error-message">{message.content}</p>;
      case 'dialogue':
        return <p className={`dialogue-message ${message.sender}`}>{message.content}</p>;
      case 'action':
        return <p className={`action-message ${message.sender}`}>*{message.content}*</p>;
      case 'thought':
        return <p className={`thought-message ${message.sender}`}><i>{message.content}</i></p>;
      default:
        return <p className={`message ${message.sender}`}>{message.content}</p>;
    }
  };

  return (
    <div className="chat-interface">
      <div className="chat-header">
        <h2>{scenario?.title || 'Untitled Scenario'}</h2>
        <div className="chat-controls">
          <button className="control-btn">
            <Settings size={18} />
          </button>
          <button className="control-btn">
            <Music size={18} />
          </button>
          <button className="control-btn">
            <BookOpen size={18} />
          </button>
        </div>
      </div>

      <div className="messages-container">
        {messages.map(message => (
          <div key={message.id} className={`message-wrapper ${message.sender}`}>
            {formatMessage(message)}
            <span className="timestamp">
              {message.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
            </span>
          </div>
        ))}

        {isTyping && (
          <div className="typing-indicator">
            <span></span>
            <span></span>
            <span></span>
          </div>
        )}

        <div ref={messagesEndRef} />
      </div>

      <div className="message-type-selector">
        <button 
          className={`type-btn ${messageType === 'dialogue' ? 'active' : ''}`}
          onClick={() => setMessageType('dialogue')}
        >
          Dialogue
        </button>
        <button 
          className={`type-btn ${messageType === 'action' ? 'active' : ''}`}
          onClick={() => setMessageType('action')}
        >
          Action
        </button>
        <button 
          className={`type-btn ${messageType === 'thought' ? 'active' : ''}`}
          onClick={() => setMessageType('thought')}
        >
          Thought
        </button>
      </div>

      <div className="input-area">
        <textarea 
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          onKeyDown={handleKeyPress}
          placeholder={messageType === 'dialogue' ? 'Type your dialogue...' : 
                       messageType === 'action' ? 'Describe your action...' : 'Share your thoughts...'}
          rows={3}
        />
        <button className="send-btn" onClick={handleSendMessage}>
          <Send size={20} />
        </button>
      </div>
    </div>
  );
};

export default ChatInterface;
