/**
 * Service for handling interactions with Google's Gemini AI API
 */
class GeminiService {
  constructor(apiKey) {
    this.apiKey = apiKey;
    // Updated to use the latest Gemini Flash model (gemini-1.5-flash is the current version)
    this.baseUrl = 'https://generativelanguage.googleapis.com/v1beta/models/gemini-1.5-flash:generateContent';
    this.history = [];
    this.characterProfile = null;
    this.scenarioDetails = null;
  }

  /**
   * Initialize the service with character and scenario information
   * @param {Object} characterProfile - The detailed character profile
   * @param {Object} scenarioDetails - The scenario setup details
   */
  initialize(characterProfile, scenarioDetails) {
    this.characterProfile = characterProfile;
    this.scenarioDetails = scenarioDetails;
    this.history = [];

    // Create system message that sets up the context
    const systemContext = this._createSystemContext();
    this.history.push({
      role: 'model',
      parts: [{ text: systemContext }]
    });

    // If there are roleplay instructions, add them as system context
    if (scenarioDetails.roleplayInstructions) {
      this.history.push({
        role: 'model',
        parts: [{ text: `Roleplay Instructions:\n\n${scenarioDetails.roleplayInstructions}` }]
      });
    }
  }

  /**
   * Update roleplay instructions
   * @param {string} instructions - New roleplay instructions
   */
  updateInstructions(instructions) {
    // Find any existing instruction message
    const existingInstructionIndex = this.history.findIndex(msg => 
      msg.role === 'model' && 
      msg.parts[0]?.text?.includes('Roleplay Instructions:')
    );

    // Create the instruction message
    const instructionMessage = {
      role: 'model',
      parts: [{ text: `Roleplay Instructions:\n\n${instructions}` }]
    };

    if (existingInstructionIndex !== -1) {
      // Replace existing instructions
      this.history[existingInstructionIndex] = instructionMessage;
    } else {
      // Add new instructions after the system context
      this.history.splice(1, 0, instructionMessage);
    }

    // Add a reminder to follow the new instructions (not visible to user)
    this.history.push({
      role: 'model',
      parts: [{ text: 'Remember to follow the updated roleplay instructions provided above, but do not explicitly mention them in your responses.' }]
    });
  }

  /**
   * Generate a generic response from Gemini based on a prompt
   * @param {string} prompt - The prompt to send to Gemini
   * @returns {Promise<string>} - The AI generated response
   */
  async generateGeneric(prompt) {
    try {
      console.log('Sending request to Gemini API');

      try {
        const response = await fetch(`${this.baseUrl}?key=${this.apiKey}`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            contents: [{
              role: 'user',
              parts: [{ text: prompt }]
            }],
            generationConfig: {
              temperature: 0.7,
              topK: 40,
              topP: 0.95,
              maxOutputTokens: 1000,
            }
          })
        });

        // Log the full URL being used (without the API key)
        console.log('Using API URL:', this.baseUrl);

        // Check HTTP status before trying to parse JSON
        if (!response.ok) {
          const errorText = await response.text();
          console.error('API error response:', errorText);
          throw new Error(`API error: ${response.status} - ${errorText || 'Unknown error'}`);
        }

        const data = await response.json();
        console.log('API response structure:', JSON.stringify(data, null, 2).substring(0, 500) + '...');

        // Extract the model's response
        if (data.candidates && data.candidates[0]?.content?.parts?.length > 0) {
          return data.candidates[0].content.parts[0].text;
        } else {
          console.error('Unexpected API response structure:', data);
          throw new Error('Invalid response format from Gemini API');
        }
      } catch (fetchError) {
        console.error('Fetch error:', fetchError);
        throw fetchError;
      }
    } catch (error) {
      console.error('Error calling Gemini API:', error);
      throw error;
    }
  }

  /**
   * Generate a response from Gemini based on user input and context
   * @param {string} userMessage - The user's message content
   * @param {string} messageType - Type of message (dialogue, action, thought)
   * @returns {Promise<string>} - The AI generated response
   */
  async generateResponse(userMessage, messageType) {
    try {
      // Add user message to history
      this.history.push({
        role: 'user',
        parts: [{ text: `[${messageType.toUpperCase()}] ${userMessage}` }]
      });

      // Prepare the context window for Gemini
      const contents = [...this.history];

      console.log('Sending roleplay request to Gemini API');

      try {
        const response = await fetch(`${this.baseUrl}?key=${this.apiKey}`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            contents: contents,
            generationConfig: {
              temperature: 0.7,
              topK: 40,
              topP: 0.95,
              maxOutputTokens: 800,
            },
            safetySettings: [
              {
                category: "HARM_CATEGORY_HARASSMENT",
                threshold: "BLOCK_MEDIUM_AND_ABOVE"
              },
              {
                category: "HARM_CATEGORY_HATE_SPEECH",
                threshold: "BLOCK_MEDIUM_AND_ABOVE"
              },
              {
                category: "HARM_CATEGORY_SEXUALLY_EXPLICIT",
                threshold: "BLOCK_ONLY_HIGH"
              },
              {
                category: "HARM_CATEGORY_DANGEROUS_CONTENT",
                threshold: "BLOCK_MEDIUM_AND_ABOVE"
              }
            ]
          })
        });

        // Check HTTP status before trying to parse JSON
        if (!response.ok) {
          const errorText = await response.text();
          console.error('API error response for roleplay:', errorText);
          throw new Error(`API error in roleplay: ${response.status} - ${errorText || 'Unknown error'}`);
        }

        const data = await response.json();

        // Extract the model's response
        if (data.candidates && data.candidates[0]?.content?.parts?.length > 0) {
          const aiResponseText = data.candidates[0].content.parts[0].text;

          // Add AI response to history
          this.history.push({
            role: 'model',
            parts: [{ text: aiResponseText }]
          });

          return aiResponseText;
        } else {
          console.error('Unexpected roleplay API response structure:', data);
          throw new Error('Invalid response format from Gemini API in roleplay');
        }
      } catch (fetchError) {
        console.error('Fetch error in roleplay:', fetchError);
        throw fetchError;
      }
    } catch (error) {
      console.error('Error calling Gemini API:', error);
      throw error;
    }
  }

  /**
   * Create the detailed system context from character and scenario
   * @private
   * @returns {string} - Formatted system context
   */
  _createSystemContext() {
    const { name, age, physicalDescription, background, personality } = this.characterProfile;
    const { title, setting, initialSituation, otherCharacters, toneAndThemes } = this.scenarioDetails;

    // Create detailed system prompt
    return `
You are roleplaying in a scenario titled "${title}".

## My Character
Name: ${name || 'Unnamed'}
Age: ${age || 'Unknown'}
Physical Description: ${physicalDescription || 'Not provided'}
Background: ${background || 'Not provided'}
Personality: ${personality || 'Not provided'}

## Scenario Setting
Location: ${setting?.location || 'Unspecified'}
Time: ${setting?.time || 'Unspecified'}
Atmosphere: ${setting?.atmosphere || 'Unspecified'}

## Initial Situation
${initialSituation || 'No initial situation provided.'}

## Other Characters
${otherCharacters?.map(char => `
- ${char.name} (${char.role}): ${char.description}
  Relationship to protagonist: ${char.relationship}
`).join('') || 'No other characters specified.'}

## Tone and Themes
${toneAndThemes || 'No specific tone or themes specified.'}

## Roleplay Instructions:
1. You are roleplaying as the characters in this scenario EXCEPT the protagonist.
2. Stay in character at all times and maintain a realistic, consistent tone.
3. Respond to the protagonist's actions, dialogue, and thoughts appropriately.
4. Ensure your responses reflect the emotional atmosphere of the scene.
5. When the protagonist communicates, they may mark their message as [DIALOGUE], [ACTION], or [THOUGHT].
6. Format your responses to clearly indicate dialogue, actions, and narrative elements.
7. Help develop the story in the direction indicated by the protagonist's choices.
8. Keep responses rich and immersive, but reasonably concise (2-5 paragraphs).
9. Be creative, emotionally resonant, and authentic to the established characters and world.
10. Never break character by discussing the nature of the roleplay itself.
`;
  }

  /**
   * Save the current chat history
   * @returns {Array} - The current chat history
   */
  exportChatHistory() {
    return [...this.history];
  }

  /**
   * Import an existing chat history
   * @param {Array} history - Previous chat history
   */
  importChatHistory(history) {
    this.history = [...history];
  }
}

export default GeminiService;
