        currentStepIndex = 2;
        break;
      case STEPS.CHARACTER_DETAIL:
        currentStepIndex = 3;
        break;
      case STEPS.CHARACTER_RELATIONSHIPS:
        currentStepIndex = 4;
        break;
      case STEPS.SCENARIO_BASIC:
        currentStepIndex = 5;
        break;
      case STEPS.SCENARIO_SETTING:
        currentStepIndex = 6;
        break;
      case STEPS.SCENARIO_CHARACTERS:
        currentStepIndex = 7;
        break;
      case STEPS.SCENARIO_GOALS:
        currentStepIndex = 8;
        break;
      case STEPS.REVIEW:
        currentStepIndex = 9;
        break;
      default:
        currentStepIndex = 1;
    }

    return (currentStepIndex / steps) * 100;
  };

  // Get the step label
  const getStepLabel = () => {
    switch (currentStep) {
      case STEPS.WELCOME:
        return 'Welcome';
      case STEPS.CHARACTER_BASIC:
        return 'Character Basics';
      case STEPS.CHARACTER_DETAIL:
        return 'Character Details';
      case STEPS.CHARACTER_RELATIONSHIPS:
        return 'Character Relationships';
      case STEPS.SCENARIO_BASIC:
        return 'Scenario Basics';
      case STEPS.SCENARIO_SETTING:
        return 'Scenario Setting';
      case STEPS.SCENARIO_CHARACTERS:
        return 'Scenario Characters';
      case STEPS.SCENARIO_GOALS:
        return 'Narrative Goals';
      case STEPS.REVIEW:
        return 'Review';
      default:
        return '';
    }
  };

  return (
    <div className="setup-wizard">
      <div className="wizard-header">
        <button className="reset-btn" onClick={onReset}>
          Start Over
        </button>
        <div className="wizard-progress">
          <div className="wizard-progress-label">{getStepLabel()}</div>
          <div className="wizard-progress-bar">
            <div 
              className="wizard-progress-fill" 
              style={{ width: `${getProgressPercentage()}%` }}
            ></div>
          </div>
        </div>
      </div>

      <div className="wizard-container">
        {getStepContent()}
      </div>

      {currentStep !== STEPS.WELCOME && currentStep !== STEPS.REVIEW && (
        <div className="wizard-navigation">
          <button 
            className="wizard-nav-btn wizard-prev-btn"
            onClick={prevStep}
          >
            <ArrowLeft size={16} />
            Previous
          </button>

          <button 
            className="wizard-nav-btn wizard-next-btn"
            onClick={nextStep}
            disabled={!canProceed()}
          >
            Next
            <ArrowRight size={16} />
          </button>
        </div>
      )}
    </div>
  );
};

export default SetupWizard;
