import React from 'react';

/**
 * Component for rendering social media messages in a line-by-line format
 * 
 * @param {Object} props - Component props
 * @param {string} props.content - The social media content to render
 * @returns {JSX.Element} - Rendered social media message
 */
const SocialMediaMessage = ({ content }) => {
  if (!content || typeof content !== 'string') {
    return <div className="social-media-content">Invalid content</div>;
  }

  // Helper function to identify platform headers
  const isPlatformHeader = (text) => {
    return text.includes('📱 Instagram') || 
           text.includes('🎵 TikTok') || 
           text.includes('▶️ YouTube') || 
           text.includes('🐦 Twitter') || 
           text.includes('👻 Snapchat');
  };

  // Normalize line breaks and add extra breaks around platform headers
  let normalizedContent = content.replace(/\r\n/g, '\n').replace(/\r/g, '\n');

  // Add extra line breaks around platform indicators
  const platformIndicators = ['📱 Instagram', '🎵 TikTok', '▶️ YouTube', '🐦 Twitter', '👻 Snapchat'];
  platformIndicators.forEach(indicator => {
    // Don't add a leading break if this indicator starts the content
    if (normalizedContent.trim().startsWith(indicator)) {
      normalizedContent = normalizedContent;
    } else {
      normalizedContent = normalizedContent.replace(new RegExp(indicator, 'g'), '\n\n' + indicator);
    }
  });

  // Split content into lines
  const lines = normalizedContent.split('\n');

  // Format individual lines with special styling
  const formattedLines = lines.map((line, index) => {
    // Remove markdown bold formatting first
    let processedLine = line.replace(/\*\*([^*]+)\*\*/g, '$1');

    // Apply basic formatting
    const formattedLine = processedLine
      .replace(/Instagram/g, '📱 Instagram')
      .replace(/TikTok/g, '🎵 TikTok')
      .replace(/YouTube/g, '▶️ YouTube')
      .replace(/Twitter/g, '🐦 Twitter')
      .replace(/Snapchat/g, '👻 Snapchat')
      .replace(/@([a-zA-Z0-9_]+)/g, '<span class="handle">@$1</span>')
      .replace(/"([^"]+)"/g, '<span class="quote">"$1"</span>')
      .replace(/\bviral\b/gi, '<span class="highlight">viral</span>')
      .replace(/(\d+)k?\s*followers/gi, '$1k followers 👥')
      .replace(/(\d+)k?\s*likes/gi, '$1k likes ❤️')
      .replace(/(\d+)k?\s*views/gi, '$1k views 👁️')
      .replace(/(\d+)k?\s*shares/gi, '$1k shares 🔄')
      .replace(/(\d+)k?\s*comments/gi, '$1k comments 💬');

    // Check if line is a platform header (includes a platform name)
    const isPlatformHeader = processedLine.match(/^\s*([\w]+):/);

    if (isPlatformHeader) {
      return (
        <div key={index} className="platform-header">
          <span dangerouslySetInnerHTML={{ __html: formattedLine }} />
        </div>
      );
    }

    // Regular message line
    return (
      <div key={index} className="social-media-message">
        <span dangerouslySetInnerHTML={{ __html: formattedLine }} />
      </div>
    );
  });

  // Group messages by platform to create better visual separation
  const groupedContent = [];
  let currentPlatform = null;
  let currentGroup = [];

  formattedLines.forEach((line, index) => {
    // Check if this line is a new platform header
    const platformMatch = typeof line.props?.dangerouslySetInnerHTML?.__html === 'string' ? 
      line.props.dangerouslySetInnerHTML.__html.match(/(Instagram|TikTok|YouTube|Twitter|Snapchat)/i) : null;

    if (platformMatch) {
      // If we have an existing group, add it to our results
      if (currentGroup.length > 0) {
        groupedContent.push(
          <div key={`group-${currentPlatform}`} className={`platform-group ${currentPlatform.toLowerCase()}`}>
            {currentGroup}
          </div>
        );
      }

      // Start a new group with this platform
      currentPlatform = platformMatch[1];
      currentGroup = [line];
    } else {
      // Add to current group
      currentGroup.push(line);
    }
  });

  // Add the last group if it exists
  if (currentGroup.length > 0 && currentPlatform) {
    groupedContent.push(
      <div key={`group-${currentPlatform}`} className={`platform-group ${currentPlatform.toLowerCase()}`}>
        {currentGroup}
      </div>
    );
  }

  return (
    <div className="social-media-content">
      {groupedContent.length > 0 ? groupedContent : formattedLines}
    </div>
  );
};

export default SocialMediaMessage;
